    private static String[] SUBFIX= {"모모", "아고", "댄마", "젤리언", "무노", "피코", "포코"};

    private static List<String> randomList = new CopyOnWriteArrayList<>();

    private Random random = new Random();

    @PostConstruct
    private void init() {
        randomList.addAll(Arrays.asList(PREFIX));
        Collections.shuffle(randomList);
    }

    public String generateName(){

        // 모모프렌즈 택 1
        int rand2 = random.nextInt(SUBFIX.length);

        return popPreFix() + " " + SUBFIX[rand2];

    }

    private String popPreFix() {
        if (randomList.size() == 0)
            init();

        int rand1 = random.nextInt(randomList.size());
        String selectPrefix = randomList.get(rand1);
        randomList.remove(rand1);

        return selectPrefix;
    }
